import React, { useEffect, useContext } from 'react';
import { Fragment } from 'react-dom';
import firebase from '../firebase';
import { Context } from '../context';

const FirebaseAuthState = ({ children }) => {

    // update state with dispatch
    const { dispatch } = useContext(Context);

    useEffect(() => {
        // gives logged in users current auth state
        return firebase.auth().onAuthStateChanged(async () => {
            if (!user) {
                dispatch({
                    type: 'LOGOUT',
                });
            } else {
                dispatch({
                    type: 'LOGIN',
                    payload: user,
                });
            }
        });
    }, [])


    return (
        <>
            {children}
        </>
    );
};

export default FirebaseAuthState;
